mapper.select:
SELECT id, type, path, file_name AS fileName, mtime, extra_info AS extraInfo, update_time AS updateTime FROM file WHERE path = #{path} ORDER BY type DESC
LIMIT ${start}, ${count};
LIMIT ${start}

mapper.delete:
DELETE FROM file


        AND path = #{path}


        AND file_name = #{fileName}


        AND type = #{type}

mapper.update:
UPDATE file
SET
    update_time = datetime('now'),
    type = #{new.type},
    path = #{new.path},
    file_name = #{new.fileName},
    mtime = #{new.mtime}
WHERE
    path = #{old.path}
    AND file_name = #{old.fileName}
